import React, { useState } from "react";
import "./App.css";
import ListItem from "./SimpleComponents/ListItem";
import moment from "moment";
import NavBar from "./SimpleComponents/NavBar";
import cover01 from "./Images/cover001.jpg";
import cover02 from "./Images/cover002.jpg";
import cover03 from "./Images/cover003.jpg";

function App() {
  const [nameList, setNameList] = useState([
    {
      id: 1,
      name: {
        title: "Mr",
        first: "Super",
        last: "Man",
      },
      location: {
        city: "Metropolis",
      },
      description:
        "Superman is a fictional superhero. The character was created by writer Jerry Siegel and artist Joe Shuster, and first appeared in Action Comics #1, a comic book published on April 18, 1938.[1] The character regularly appears in comic books published by DC Comics, and has been adapted to a number of radio serials, movies, and television shows. Superman was born on the planet Krypton and was given the name Kal-El at birth. As a baby, his parents sent him to Earth in a small spaceship moments before Krypton was destroyed in a natural cataclysm. His ship landed in the American countryside, near the fictional town of Smallville. He was found and adopted by farmers Jonathan and Martha Kent, who named him Clark Kent. Clark developed various superhuman abilities, such as incredible strength and impervious skin. His foster parents advised him to use his abilities for the benefit of humanity, and he decided to fight crime as a vigilante. To protect his privacy, he changes into a colorful costume and uses the alias 'Superman' when fighting crime. Clark Kent resides in the fictional American city of Metropolis, where he works as a journalist for the Daily Planet. Superman's supporting characters include his love interest and fellow journalist Lois Lane, Daily Planet photographer Jimmy Olsen and editor-in-chief Perry White. His most well-known villain is Lex Luthor. Superman is part of the DC Universe, and as such often appears in stories alongside other DC Universe heroes such as Batman and Wonder Woman.",
      email: "super.man@example.com",
      dob: {
        date: "1753-07-20T09:44:18.674Z",
      },
      picture: {
        medium: require("./Images/user001.jpg"),
      },
    },

    {
      id: 2,
      name: {
        title: "Mr",
        first: "Bat",
        last: "Man",
      },
      location: {
        city: "Ghothem City",
      },
      description:
        "Batman is a fictional superhero appearing in American comic books published by DC Comics. The character was created by artist Bob Kane and writer Bill Finger,[2][3] and first appeared in Detective Comics #27 in 1939. Originally named the \"Bat-Man,\" the character is also referred to by such epithets as the Caped Crusader, the Dark Knight, and the World's Greatest Detective. Batman's secret identity is Bruce Wayne, a wealthy American playboy, philanthropist, and owner of Wayne Enterprises. Batman originated from an incident in Bruce's childhood; after witnessing the murder of his parents Dr. Thomas Wayne and Martha Wayne, he swore vengeance against criminals, an oath tempered by a sense of justice. Bruce trains himself physically and intellectually and crafts a bat-inspired persona to fight crime. Batman operates in the fictional Gotham City with assistance from various supporting characters, including his butler Alfred, police commissioner Jim Gordon, and vigilante allies such as Robin. Unlike most superheroes, Batman does not possess any inhuman superpowers. He does, however, possess a genius-level intellect and is a peerless martial artist, and his vast wealth affords him an extraordinary arsenal of weaponry and equipment. A large assortment of villains make up Batman's rogues gallery, including his nemesis, the Joker.",
      email: "bat.man@example.com",
      dob: {
        date: "1970-08-23T01:14:28.374Z",
      },
      picture: {
        medium: require("./Images/user002.jpg"),
      },
    },

    {
      id: 3,
      name: {
        title: "Mr",
        first: "Aqua",
        last: "Man",
      },
      location: {
        city: "Atlantis",
      },
      description:
        "Aquaman (Arthur Curry) is a fictional superhero appearing in American comic books published by DC Comics. Created by Paul Norris and Mort Weisinger, the character debuted in More Fun Comics #73 (November 1941). Initially a backup feature in DC's anthology titles, Aquaman later starred in several volumes of a solo comic book series. During the late 1950s' and 1960s' superhero-revival period known as the Silver Age, he was a founding member of the Justice League. In the 1990s' Modern Age, writers interpreted Aquaman's character more seriously, with storylines depicting the weight of his role as king of Atlantis. The character's original 1960s animated appearances left a lasting impression, making Aquaman widely recognized in popular culture and one of the world's most recognized superheroes. Jokes about his wholesome, weak portrayal in Super Friends and perceived feeble powers and abilities have been staples of comedy programs and stand-up routines, leading DC at several times to attempt to make the character edgier or more powerful in comic books. Modern comic book depictions have attempted to reconcile these various aspects of his public perception, with many versions often casting Aquaman as serious and brooding, saddled with an ill reputation, and struggling to find a true role and purpose beyond his public side as a deposed king and a fallen hero.",
      email: "aqua.man@example.com",
      dob: {
        date: "1977-12-22T01:14:28.374Z",
      },
      picture: {
        medium: require("./Images/user003.jpg"),
      },
    },

    {
      id: 4,
      name: {
        title: "Miss",
        first: "Wonder",
        last: "Woman",
      },
      location: {
        city: "Themyscira",
      },
      description:
      "Wonder Woman is a fictional superhero appearing in American comic books published by DC Comics. The character is a founding member of the Justice League. The character first appeared in All Star Comics #8 in October 1941[1] with her first feature in Sensation Comics #1 in January 1942. The Wonder Woman title has been published by DC Comics almost continuously except for a brief hiatus in 1986.[3] In her homeland, the island nation of Themyscira, her official title is Princess Diana of Themyscira. When blending into the society outside of her homeland, she sometimes adopts her civilian identity Diana Prince. Wonder Woman was created by the American psychologist and writer William Moulton Marston (pen name: Charles Moulton), and artist Harry G. Peter. Marston's wife, Elizabeth, and their life partner, Olive Byrne, are credited as being his inspiration for the character's appearance. Marston's comics featured his ideas on DISC theory, and the character drew a great deal of inspiration from early feminists, and especially from birth control pioneer Margaret Sanger; in particular, her piece \"Woman and the New Race\". Wonder Woman's origin story relates that she was sculpted from clay by her mother Queen Hippolyta and was given a life to live as an Amazon, along with superhuman powers as gifts by the Greek gods.",      email: "wonder.woman@example.com",
      dob: {
        date: "1898-01-01T09:44:18.674Z",
      },
      picture: {
        medium: require("./Images/user004.jpg"),
      },
    },
  ]);

  const nameListComponent = () => {
    return nameList.map((card) => {
      return (
        <ListItem
          key={card.id}
          name={`${card.name.first} ${card.name.last}`}
          city={card.location.city}
          email={card.email}
          birthday={moment(card.dob.date).format("L")}
          avatar={card.picture.medium}
          description={card.description}
        />
      );
    });
  };

  const addHeroHandler = () => {
    const newHero = {
      id: 4,
      name: {
        title: "Mr",
        first: "The",
        last: "Flash",
      },
      location: {
        city: "Central City",
      },
      description: "The Flash (or simply Flash) is the name of several superheroes appearing in American comic books published by DC Comics. Created by writer Gardner Fox and artist Harry Lampert, the original Flash first appeared in Flash Comics #1 (cover date January 1940/release month November 1939). Nicknamed the \"Scarlet Speedster\", all incarnations of the Flash possess \"super speed\", which includes the ability to run, move, and think extremely fast, use superhuman reflexes, and seemingly violate certain laws of physics. \n Thus far, at least four different characters—each of whom somehow gained the power of \"the speed force\"—have assumed the mantle of the Flash in DC's history: college athlete Jay Garrick (1940–1951, 1961–2011, 2017–present), forensic scientist Barry Allen (1956–1985, 2008–present), Barry's nephew Wally West (1986–2011, 2016–present), and Barry's grandson Bart Allen (2006–2007). Each incarnation of the Flash has been a key member of at least one of DC's premier teams: the Justice Society of America, the Justice League, and the Teen Titans.",
      email: "the.flash@example.com",
      dob: {
        date: "1997-12-01T09:44:18.674Z",
      },
      picture: {
        medium: require("./Images/user005.jpg"),
      },
    };
    setNameList(nameList.concat(newHero));
  }

  return (
    <React.Fragment>
      <div className="PageColor">
        <NavBar />
        <div
          id="carouselExampleIndicators"
          className="carousel slide Carousel"
          data-ride="carousel"
        >
          <ol className="carousel-indicators">
            <li
              data-target="#carouselExampleIndicators"
              data-slide-to="0"
              className="active"
            ></li>
            <li data-target="#carouselExampleIndicators" data-slide-to="1"></li>
            <li data-target="#carouselExampleIndicators" data-slide-to="2"></li>
          </ol>
          <div className="carousel-inner">
            <div className="carousel-item active">
              <img src={cover01} className="d-block w-100 Slider" alt="..." />
            </div>
            <div className="carousel-item">
              <img src={cover02} className="d-block w-100 Slider" alt="..." />
            </div>
            <div className="carousel-item">
              <img src={cover03} className="d-block w-100 Slider" alt="..." />
            </div>
          </div>
          <a
            className="carousel-control-prev"
            href="#carouselExampleIndicators"
            role="button"
            data-slide="prev"
          >
            <span
              className="carousel-control-prev-icon"
              aria-hidden="true"
            ></span>
            <span className="sr-only">Previous</span>
          </a>
          <a
            className="carousel-control-next"
            href="#carouselExampleIndicators"
            role="button"
            data-slide="next"
          >
            <span
              className="carousel-control-next-icon"
              aria-hidden="true"
            ></span>
            <span className="sr-only">Next</span>
          </a>
        </div>
        <div className="Container">
          <button className="btn btn-dark Button" onClick={addHeroHandler}>Add another DC comic super hero</button>
          <div className="Page">{nameListComponent()}</div>
        </div>
      </div>
    </React.Fragment>
  );
}

export default App;
